        Monday: "",
        Tuesday: "",
        Wednesday: "",
        Thursday: "",
        Friday: ""
      },
      {
        key: "1",
        Hours: "14-15",
        Monday: "",
        Tuesday: "",
        Wednesday: "",
        Thursday: "",
        Friday: ""
      },
      {
        key: "1",
        Hours: "15-16",
        Monday: "",
        Tuesday: "",
        Wednesday: "",
        Thursday: "",
        Friday: ""
      },
      {
        key: "1",
        Hours: "16-17",
        Monday: "",
        Tuesday: "",
        Wednesday: "",
        Thursday: "",
        Friday: ""
      },
      {
        key: "1",
        Hours: "17-18",
        Monday: "",
        Tuesday: "",
        Wednesday: "",
        Thursday: "",
        Friday: ""
      }
    ];
    const columns1 = [
      {
        title: "LecCode",
        dataIndex: "LecCode",
        key: "LecCode",

        ...this.getColumnSearchProps("LecCode")
      },
      {
        title: "LecName",
        dataIndex: "LecName",
        key: "LecName",

        ...this.getColumnSearchProps("LecName")
      },
      {
        title: "Days",
        dataIndex: "Days",
        key: "Days"
      },
      {
        title: "Hours",
        dataIndex: "Hours",
        key: "Hours"
      },
      {
        title: "Instructor",
        dataIndex: "Instructor",
        key: "Instructor"
      }
    ];
    return (
      <div>
        <img
          src={boun}
          alt="Logo"
          style={{
            overflowY: "auto",
            position: "fixed",
            top: 0,
            left: 0,
            backgroundSize: "cover",
            zIndex: -1,
            width: "100%",
            height: "100%"
          }}
        />
        <div
          style={{ width: "100%", height: "100%", position: "absolute", top: 0, left: 0, backgroundColor: "#00000090" }}
        />

        <div
          style={{
            marginTop: 30
          }}
        >
          <Row type="flex" justify="space-around">
            <Col span={10}>
              <div style={{ top: 0, left: 0, backgroundColor: "#FFFFFF90" }}>
                <Table bordered="true" columns={columns} dataSource={data} />
              </div>
            </Col>
            <Col span={10}>
              <div
                style={{
                  top: 0,
                  left: 0,
                  backgroundColor: "#FFFFFF90"
                }}
              >
                <Table bordered="true" columns={columns1} dataSource={data1} />
              </div>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}
function mapStateToProps(appState, ownProps) {
  return {
    ui: appState.ui
  };
}

function mapDispatchToProps(dispatch) {
  return {
    ...bindActionCreators(
      {
        fetchLessonsRequest,
        fetchLessonsReset
      },
      dispatch
    )
  };
}
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(TablePage);
